<?php
use Silex\Application;
use \Symfony\Component\HttpFoundation\Request;
use api\controller\CubeController;

class ControllerTest extends \App_TestCase {

	private $app;

	protected function tearDown(){
		if(file_exists('./db.json')) {
			unlink('./db.json');
		}
	}

	public function setUp() {
		copy("./api/db.json", "./db.json");
		$app = new Application();
		$app['session.test'] = true;
		new \api\controller\MasterController($app);
		return $this->app = $app;
	}

	public function testIndex(){
		$cubeController = new CubeController();
		$this->assertEquals(200, $cubeController->index($this->app)->getStatusCode());

		unlink('./db.json');

		$this->assertEquals(204, $cubeController->index($this->app)->getStatusCode());
	}

	public function testShow(){
		$cubeController = new CubeController();
		$this->assertEquals(200, $cubeController->show('compress',$this->app)->getStatusCode());

		unlink('./db.json');

		$this->assertEquals(400, $cubeController->show('compress',$this->app)->getStatusCode());
	}

